// Invoice Generator
// Creates invoice data structure for different billing types

const client = $input.first().json;
const billingDate = new Date();

const invoice = {
  invoiceNumber: `INV-${billingDate.getFullYear()}${String(billingDate.getMonth() + 1).padStart(2, '0')}-${client.client_id}-${Date.now().toString().slice(-4)}`,

  // Client details
  client: {
    id: client.client_id,
    name: client.client_name,
    email: client.billing_email || client.email,
    address: client.billing_address,
    taxId: client.tax_id,
    stripeCustomerId: client.stripe_customer_id
  },

  // Invoice details
  issueDate: billingDate.toISOString().split('T')[0],
  dueDate: new Date(billingDate.getTime() + 30*24*60*60*1000).toISOString().split('T')[0],
  currency: client.currency || 'USD',

  // Line items
  lineItems: [],

  // Billing type specific
  billingType: client.billing_type, // recurring, milestone, hourly, one-time
  billingPeriod: {
    start: new Date(billingDate.getFullYear(), billingDate.getMonth() - 1, 1).toISOString().split('T')[0],
    end: new Date(billingDate.getFullYear(), billingDate.getMonth(), 0).toISOString().split('T')[0]
  },

  // Totals (calculated after line items)
  subtotal: 0,
  taxRate: parseFloat(client.tax_rate) || 0,
  taxAmount: 0,
  discount: parseFloat(client.discount) || 0,
  total: 0,

  // Payment
  paymentMethod: client.payment_method || 'stripe',
  status: 'draft',

  // Notes
  notes: client.invoice_notes || '',
  terms: client.payment_terms || 'Net 30'
};

// Add line items based on billing type
switch (client.billing_type) {
  case 'recurring':
    // Retainer or subscription
    invoice.lineItems.push({
      description: client.service_description || 'Monthly Retainer Services',
      quantity: 1,
      unitPrice: parseFloat(client.monthly_rate) || 0,
      amount: parseFloat(client.monthly_rate) || 0
    });

    // Add any overage charges
    if (client.overage_hours > 0) {
      invoice.lineItems.push({
        description: `Additional Hours (${client.overage_hours} hrs @ $${client.hourly_rate}/hr)`,
        quantity: parseFloat(client.overage_hours),
        unitPrice: parseFloat(client.hourly_rate),
        amount: parseFloat(client.overage_hours) * parseFloat(client.hourly_rate)
      });
    }
    break;

  case 'milestone':
    // Project milestone
    invoice.lineItems.push({
      description: `${client.project_name}: ${client.milestone_name}`,
      quantity: 1,
      unitPrice: parseFloat(client.milestone_amount) || 0,
      amount: parseFloat(client.milestone_amount) || 0
    });
    break;

  case 'hourly':
    // Time-based billing
    const hours = parseFloat(client.hours_worked) || 0;
    const rate = parseFloat(client.hourly_rate) || 0;
    invoice.lineItems.push({
      description: `Professional Services - ${hours} hours`,
      quantity: hours,
      unitPrice: rate,
      amount: hours * rate
    });
    break;
}

// Add any recurring add-ons
if (client.addons) {
  const addons = typeof client.addons === 'string' ? JSON.parse(client.addons) : client.addons;
  addons.forEach(addon => {
    invoice.lineItems.push({
      description: addon.name,
      quantity: addon.quantity || 1,
      unitPrice: addon.price,
      amount: (addon.quantity || 1) * addon.price
    });
  });
}

// Calculate totals
invoice.subtotal = invoice.lineItems.reduce((sum, item) => sum + item.amount, 0);
invoice.taxAmount = invoice.subtotal * (invoice.taxRate / 100);
invoice.total = invoice.subtotal + invoice.taxAmount - invoice.discount;

return [{ json: invoice }];

// Process Stripe Payment Events

const event = $input.first().json;
const eventType = event.type;
const invoiceData = event.data?.object || {};

const paymentEvent = {
  eventType,
  eventId: event.id,
  invoiceId: invoiceData.id,
  invoiceNumber: invoiceData.metadata?.invoice_number || invoiceData.number,
  customerId: invoiceData.customer,
  amount: (invoiceData.amount_paid || invoiceData.amount_due || 0) / 100,
  currency: invoiceData.currency?.toUpperCase(),
  status: invoiceData.status,
  paidAt: invoiceData.status_transitions?.paid_at 
    ? new Date(invoiceData.status_transitions.paid_at * 1000).toISOString() 
    : null,
  hostedInvoiceUrl: invoiceData.hosted_invoice_url,
  pdfUrl: invoiceData.invoice_pdf,
  processedAt: new Date().toISOString()
};

// Determine notification type
switch (eventType) {
  case 'invoice.payment_succeeded':
    paymentEvent.notificationType = 'payment_success';
    paymentEvent.message = `Payment of $${paymentEvent.amount} received for invoice ${paymentEvent.invoiceNumber}`;
    break;
  case 'invoice.payment_failed':
    paymentEvent.notificationType = 'payment_failed';
    paymentEvent.message = `Payment failed for invoice ${paymentEvent.invoiceNumber}. Amount: $${paymentEvent.amount}`;
    paymentEvent.failureReason = invoiceData.last_finalization_error?.message || 'Unknown error';
    break;
  case 'customer.subscription.updated':
    paymentEvent.notificationType = 'subscription_updated';
    paymentEvent.message = `Subscription updated for customer ${paymentEvent.customerId}`;
    break;
  case 'customer.subscription.deleted':
    paymentEvent.notificationType = 'subscription_cancelled';
    paymentEvent.message = `Subscription cancelled for customer ${paymentEvent.customerId}`;
    break;
}

return [{ json: paymentEvent }];

