// Process overdue invoices for reminders

const overdueInvoices = $input.first().json.data || [];

return overdueInvoices.map(invoice => {
  const daysOverdue = Math.floor((Date.now() / 1000 - invoice.due_date) / (24 * 60 * 60));

  return {
    json: {
      invoiceId: invoice.id,
      invoiceNumber: invoice.number,
      customerId: invoice.customer,
      customerEmail: invoice.customer_email,
      amount: invoice.amount_due / 100,
      currency: invoice.currency.toUpperCase(),
      dueDate: new Date(invoice.due_date * 1000).toISOString().split('T')[0],
      daysOverdue,
      hostedInvoiceUrl: invoice.hosted_invoice_url,
      reminderLevel: daysOverdue <= 7 ? 'gentle' : daysOverdue <= 14 ? 'firm' : 'urgent'
    }
  };
});
