// Review Aggregator & Normalizer
// Combines reviews from all sources into unified format

const googleReviews = $('Fetch Google Reviews').first()?.json?.reviews || [];
const tripadvisorReviews = $('Fetch TripAdvisor Reviews').first()?.json?.data || [];
const yelpReviews = $('Fetch Yelp Reviews').first()?.json?.reviews || [];

const normalizedReviews = [];

// Process Google Reviews
googleReviews.forEach(review => {
  normalizedReviews.push({
    id: review.reviewId,
    source: 'google',
    rating: parseInt(review.starRating?.replace('_', '')) || convertStarRating(review.starRating),
    author: review.reviewer?.displayName || 'Anonymous',
    text: review.comment || '',
    date: review.createTime,
    replied: !!review.reviewReply,
    replyText: review.reviewReply?.comment || null,
    profileUrl: review.reviewer?.profilePhotoUrl,
    language: detectLanguage(review.comment)
  });
});

// Process TripAdvisor Reviews
tripadvisorReviews.forEach(review => {
  normalizedReviews.push({
    id: review.id,
    source: 'tripadvisor',
    rating: review.rating,
    author: review.user?.username || 'Traveler',
    text: review.text || '',
    title: review.title || '',
    date: review.published_date,
    replied: !!review.owner_response,
    replyText: review.owner_response?.text || null,
    travelType: review.trip_type,
    language: review.lang
  });
});

// Process Yelp Reviews
yelpReviews.forEach(review => {
  normalizedReviews.push({
    id: review.id,
    source: 'yelp',
    rating: review.rating,
    author: review.user?.name || 'User',
    text: review.text || '',
    date: review.time_created,
    replied: false, // Yelp doesn't expose reply status in API
    profileUrl: review.user?.image_url,
    language: detectLanguage(review.text)
  });
});

function convertStarRating(starRating) {
  const map = { 'ONE': 1, 'TWO': 2, 'THREE': 3, 'FOUR': 4, 'FIVE': 5 };
  return map[starRating] || 3;
}

function detectLanguage(text) {
  if (!text) return 'en';
  // Simple detection - would use proper API in production
  const turkishChars = /[ƒü√º≈üƒ±√∂√ßƒû√ú≈ûƒ∞√ñ√á]/;
  if (turkishChars.test(text)) return 'tr';
  return 'en';
}

// Filter unreplied reviews and sort by urgency
const unrepliedReviews = normalizedReviews
  .filter(r => !r.replied)
  .sort((a, b) => {
    // Prioritize: negative reviews first, then by date
    if (a.rating <= 2 && b.rating > 2) return -1;
    if (b.rating <= 2 && a.rating > 2) return 1;
    return new Date(b.date) - new Date(a.date);
  });

// Calculate aggregate metrics
const metrics = {
  totalReviews: normalizedReviews.length,
  unrepliedCount: unrepliedReviews.length,
  averageRating: (normalizedReviews.reduce((sum, r) => sum + r.rating, 0) / normalizedReviews.length || 0).toFixed(2),
  bySource: {
    google: normalizedReviews.filter(r => r.source === 'google').length,
    tripadvisor: normalizedReviews.filter(r => r.source === 'tripadvisor').length,
    yelp: normalizedReviews.filter(r => r.source === 'yelp').length
  },
  ratingDistribution: {
    5: normalizedReviews.filter(r => r.rating === 5).length,
    4: normalizedReviews.filter(r => r.rating === 4).length,
    3: normalizedReviews.filter(r => r.rating === 3).length,
    2: normalizedReviews.filter(r => r.rating === 2).length,
    1: normalizedReviews.filter(r => r.rating === 1).length
  },
  negativeReviewsUnreplied: unrepliedReviews.filter(r => r.rating <= 2).length
};

return [{
  json: {
    reviews: unrepliedReviews,
    metrics,
    fetchedAt: new Date().toISOString()
  }
}];

