// Weekly Review Performance Report
// Aggregates weekly metrics and trends

const allReviews = $('Aggregate & Normalize Reviews').first()?.json || { reviews: [], metrics: {} };

// Calculate weekly trends (would use historical data in production)
const weeklyReport = {
  period: {
    start: new Date(Date.now() - 7 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
    end: new Date().toISOString().split('T')[0]
  },
  metrics: allReviews.metrics,
  trends: {
    ratingChange: '+0.2', // Would calculate from historical
    reviewVolumeChange: '+15%',
    responseRateChange: '+5%'
  },
  topPraises: [],
  topComplaints: [],
  responseRate: allReviews.metrics.totalReviews > 0 
    ? ((allReviews.metrics.totalReviews - allReviews.metrics.unrepliedCount) / allReviews.metrics.totalReviews * 100).toFixed(1)
    : 100,
  revenueImpact: {
    estimatedFromRating: `${(parseFloat(allReviews.metrics.averageRating) * 2 - 1).toFixed(1)}%`,
    note: '1-star improvement = 5-9% revenue increase'
  },
  generatedAt: new Date().toISOString()
};

return [{ json: weeklyReport }];
