// Initialize audit configuration
const input = $input.first().json;

const auditConfig = {
  auditId: `AUDIT-${Date.now().toString(36).toUpperCase()}`,
  domain: input.domain || input.body?.domain || $vars.DEFAULT_AUDIT_DOMAIN,
  depth: input.depth || input.body?.depth || 'full',
  maxPages: input.maxPages || input.body?.maxPages || 500,
  categories: input.categories || input.body?.categories || [
    'technical',
    'content',
    'performance',
    'mobile',
    'security',
    'indexability'
  ],
  startedAt: new Date().toISOString(),
  previousAuditId: input.previousAuditId || null
};

// Issue severity weights
auditConfig.severityWeights = {
  critical: 10,
  high: 5,
  medium: 3,
  low: 1,
  info: 0
};

return [{ json: auditConfig }];

// Analyze technical SEO issues
const config = $('Initialize Audit').first().json;
const crawlData = $('Crawl Website').first().json;

const pages = crawlData.pages || [];
const issues = [];

// Check each page for technical issues
pages.forEach(page => {
  // Title tag issues
  if (!page.title) {
    issues.push({
      type: 'missing_title',
      severity: 'critical',
      category: 'content',
      url: page.url,
      message: 'Page is missing a title tag',
      recommendation: 'Add a unique, descriptive title tag (50-60 characters)'
    });
  } else if (page.title.length < 30) {
    issues.push({
      type: 'short_title',
      severity: 'medium',
      category: 'content',
      url: page.url,
      message: `Title tag is too short (${page.title.length} chars)`,
      recommendation: 'Expand title to 50-60 characters for better SEO'
    });
  } else if (page.title.length > 60) {
    issues.push({
      type: 'long_title',
      severity: 'low',
      category: 'content',
      url: page.url,
      message: `Title tag is too long (${page.title.length} chars)`,
      recommendation: 'Shorten title to under 60 characters to avoid truncation'
    });
  }

  // Meta description issues
  if (!page.metaDescription) {
    issues.push({
      type: 'missing_meta_description',
      severity: 'high',
      category: 'content',
      url: page.url,
      message: 'Page is missing a meta description',
      recommendation: 'Add a compelling meta description (150-160 characters)'
    });
  } else if (page.metaDescription.length < 70) {
    issues.push({
      type: 'short_meta_description',
      severity: 'medium',
      category: 'content',
      url: page.url,
      message: `Meta description is too short (${page.metaDescription.length} chars)`,
      recommendation: 'Expand meta description to 150-160 characters'
    });
  }

  // H1 tag issues
  if (!page.h1 || page.h1.length === 0) {
    issues.push({
      type: 'missing_h1',
      severity: 'high',
      category: 'content',
      url: page.url,
      message: 'Page is missing an H1 tag',
      recommendation: 'Add a single, descriptive H1 tag'
    });
  } else if (page.h1.length > 1) {
    issues.push({
      type: 'multiple_h1',
      severity: 'medium',
      category: 'content',
      url: page.url,
      message: `Page has ${page.h1.length} H1 tags`,
      recommendation: 'Use only one H1 tag per page'
    });
  }

  // Canonical issues
  if (!page.canonical) {
    issues.push({
      type: 'missing_canonical',
      severity: 'medium',
      category: 'indexability',
      url: page.url,
      message: 'Page is missing a canonical tag',
      recommendation: 'Add a self-referencing canonical tag'
    });
  }

  // Broken links
  if (page.brokenLinks && page.brokenLinks.length > 0) {
    page.brokenLinks.forEach(link => {
      issues.push({
        type: 'broken_link',
        severity: 'high',
        category: 'technical',
        url: page.url,
        message: `Broken link found: ${link.href} (${link.statusCode})`,
        recommendation: 'Fix or remove the broken link'
      });
    });
  }

  // Image alt text
  if (page.images) {
    page.images.filter(img => !img.alt).forEach(img => {
      issues.push({
        type: 'missing_alt_text',
        severity: 'medium',
        category: 'content',
        url: page.url,
        message: `Image missing alt text: ${img.src}`,
        recommendation: 'Add descriptive alt text to all images'
      });
    });
  }

  // HTTPS issues
  if (page.url.startsWith('http://')) {
    issues.push({
      type: 'not_https',
      severity: 'critical',
      category: 'security',
      url: page.url,
      message: 'Page is not served over HTTPS',
      recommendation: 'Migrate to HTTPS for security and SEO benefits'
    });
  }

  // Response time issues
  if (page.responseTime > 3000) {
    issues.push({
      type: 'slow_response',
      severity: 'high',
      category: 'performance',
      url: page.url,
      message: `Slow server response (${page.responseTime}ms)`,
      recommendation: 'Optimize server response time to under 200ms'
    });
  }

  // Word count issues
  if (page.wordCount && page.wordCount < 300) {
    issues.push({
      type: 'thin_content',
      severity: 'medium',
      category: 'content',
      url: page.url,
      message: `Thin content (${page.wordCount} words)`,
      recommendation: 'Expand content to at least 300 words for better rankings'
    });
  }

  // Redirect chain issues
  if (page.redirectChain && page.redirectChain.length > 1) {
    issues.push({
      type: 'redirect_chain',
      severity: 'medium',
      category: 'technical',
      url: page.url,
      message: `Redirect chain detected (${page.redirectChain.length} hops)`,
      recommendation: 'Update links to point directly to final URL'
    });
  }
});

// Check for duplicate content
const titleCounts = {};
const descriptionCounts = {};

pages.forEach(page => {
  if (page.title) {
    titleCounts[page.title] = (titleCounts[page.title] || []);
    titleCounts[page.title].push(page.url);
  }
  if (page.metaDescription) {
    descriptionCounts[page.metaDescription] = (descriptionCounts[page.metaDescription] || []);
    descriptionCounts[page.metaDescription].push(page.url);
  }
});

Object.entries(titleCounts).filter(([_, urls]) => urls.length > 1).forEach(([title, urls]) => {
  issues.push({
    type: 'duplicate_title',
    severity: 'high',
    category: 'content',
    urls,
    message: `Duplicate title found on ${urls.length} pages: "${title.substring(0, 50)}..."`,
    recommendation: 'Create unique titles for each page'
  });
});

Object.entries(descriptionCounts).filter(([_, urls]) => urls.length > 1).forEach(([desc, urls]) => {
  issues.push({
    type: 'duplicate_description',
    severity: 'medium',
    category: 'content',
    urls,
    message: `Duplicate meta description found on ${urls.length} pages`,
    recommendation: 'Create unique descriptions for each page'
  });
});

return [{ json: {
  config,
  crawlSummary: {
    totalPages: pages.length,
    avgResponseTime: pages.reduce((a, b) => a + (b.responseTime || 0), 0) / pages.length,
    totalLinks: pages.reduce((a, b) => a + (b.links?.length || 0), 0)
  },
  issues
}}];

// Analyze PageSpeed results
const mobileResults = $('PageSpeed Mobile').first().json;
const desktopResults = $('PageSpeed Desktop').first().json;
const config = $('Initialize Audit').first().json;

const issues = [];

// Process mobile performance
const mobilePerf = mobileResults.lighthouseResult?.categories?.performance?.score * 100 || 0;
const desktopPerf = desktopResults.lighthouseResult?.categories?.performance?.score * 100 || 0;

if (mobilePerf < 50) {
  issues.push({
    type: 'poor_mobile_performance',
    severity: 'critical',
    category: 'performance',
    url: `https://${config.domain}`,
    message: `Poor mobile performance score: ${mobilePerf}/100`,
    recommendation: 'Optimize images, reduce JavaScript, and improve Core Web Vitals'
  });
} else if (mobilePerf < 90) {
  issues.push({
    type: 'moderate_mobile_performance',
    severity: 'medium',
    category: 'performance',
    url: `https://${config.domain}`,
    message: `Mobile performance needs improvement: ${mobilePerf}/100`,
    recommendation: 'Review Core Web Vitals and optimize critical rendering path'
  });
}

// Core Web Vitals
const cwv = mobileResults.lighthouseResult?.audits;
if (cwv) {
  // LCP (Largest Contentful Paint)
  const lcp = cwv['largest-contentful-paint']?.numericValue;
  if (lcp > 4000) {
    issues.push({
      type: 'poor_lcp',
      severity: 'critical',
      category: 'performance',
      url: `https://${config.domain}`,
      message: `Poor LCP: ${(lcp/1000).toFixed(1)}s (should be under 2.5s)`,
      recommendation: 'Optimize largest contentful paint element, consider lazy loading'
    });
  } else if (lcp > 2500) {
    issues.push({
      type: 'needs_improvement_lcp',
      severity: 'high',
      category: 'performance',
      url: `https://${config.domain}`,
      message: `LCP needs improvement: ${(lcp/1000).toFixed(1)}s`,
      recommendation: 'Improve server response time and optimize resource loading'
    });
  }

  // FID / TBT (Total Blocking Time as proxy)
  const tbt = cwv['total-blocking-time']?.numericValue;
  if (tbt > 600) {
    issues.push({
      type: 'poor_tbt',
      severity: 'critical',
      category: 'performance',
      url: `https://${config.domain}`,
      message: `High Total Blocking Time: ${tbt}ms (should be under 200ms)`,
      recommendation: 'Reduce JavaScript execution time, split long tasks'
    });
  }

  // CLS (Cumulative Layout Shift)
  const cls = cwv['cumulative-layout-shift']?.numericValue;
  if (cls > 0.25) {
    issues.push({
      type: 'poor_cls',
      severity: 'high',
      category: 'performance',
      url: `https://${config.domain}`,
      message: `High layout shift: ${cls.toFixed(3)} (should be under 0.1)`,
      recommendation: 'Set dimensions for images and embeds, avoid inserting content above existing content'
    });
  }
}

// Accessibility issues
const accessibilityScore = mobileResults.lighthouseResult?.categories?.accessibility?.score * 100 || 0;
if (accessibilityScore < 90) {
  issues.push({
    type: 'accessibility_issues',
    severity: accessibilityScore < 50 ? 'critical' : 'high',
    category: 'accessibility',
    url: `https://${config.domain}`,
    message: `Accessibility score: ${accessibilityScore}/100`,
    recommendation: 'Review and fix accessibility issues for better usability'
  });
}

// SEO score from PageSpeed
const seoScore = mobileResults.lighthouseResult?.categories?.seo?.score * 100 || 0;
if (seoScore < 90) {
  issues.push({
    type: 'pagespeed_seo_issues',
    severity: 'high',
    category: 'technical',
    url: `https://${config.domain}`,
    message: `PageSpeed SEO score: ${seoScore}/100`,
    recommendation: 'Review Lighthouse SEO audit for specific improvements'
  });
}

const performanceData = {
  mobile: {
    performance: mobilePerf,
    accessibility: accessibilityScore,
    bestPractices: mobileResults.lighthouseResult?.categories?.['best-practices']?.score * 100 || 0,
    seo: seoScore,
    lcp: cwv?.['largest-contentful-paint']?.numericValue,
    tbt: cwv?.['total-blocking-time']?.numericValue,
    cls: cwv?.['cumulative-layout-shift']?.numericValue
  },
  desktop: {
    performance: desktopPerf,
    accessibility: desktopResults.lighthouseResult?.categories?.accessibility?.score * 100 || 0,
    bestPractices: desktopResults.lighthouseResult?.categories?.['best-practices']?.score * 100 || 0,
    seo: desktopResults.lighthouseResult?.categories?.seo?.score * 100 || 0
  }
};

return [{ json: { performanceData, issues } }];

// Compile full audit report
const technicalData = $('Technical SEO Analysis').first().json;
const performanceData = $('Performance Analysis').first().json;
const config = technicalData.config;

// Combine all issues
const allIssues = [
  ...technicalData.issues,
  ...performanceData.issues
];

// Calculate scores by category
const categoryScores = {};
const categories = ['technical', 'content', 'performance', 'mobile', 'security', 'indexability', 'accessibility'];

categories.forEach(cat => {
  const catIssues = allIssues.filter(i => i.category === cat);
  const penalty = catIssues.reduce((sum, issue) => {
    return sum + (config.severityWeights[issue.severity] || 0);
  }, 0);
  categoryScores[cat] = Math.max(0, 100 - penalty);
});

// Overall health score
const overallScore = Math.round(
  Object.values(categoryScores).reduce((a, b) => a + b, 0) / categories.length
);

// Group issues by severity
const issuesBySeverity = {
  critical: allIssues.filter(i => i.severity === 'critical'),
  high: allIssues.filter(i => i.severity === 'high'),
  medium: allIssues.filter(i => i.severity === 'medium'),
  low: allIssues.filter(i => i.severity === 'low')
};

// Top priorities
const priorities = [
  ...issuesBySeverity.critical.slice(0, 5),
  ...issuesBySeverity.high.slice(0, 5)
].slice(0, 10);

const auditReport = {
  auditId: config.auditId,
  domain: config.domain,
  completedAt: new Date().toISOString(),
  startedAt: config.startedAt,
  summary: {
    overallScore,
    categoryScores,
    totalIssues: allIssues.length,
    criticalIssues: issuesBySeverity.critical.length,
    highIssues: issuesBySeverity.high.length,
    mediumIssues: issuesBySeverity.medium.length,
    lowIssues: issuesBySeverity.low.length,
    pagesAnalyzed: technicalData.crawlSummary.totalPages
  },
  performance: performanceData.performanceData,
  topPriorities: priorities,
  issuesBySeverity,
  allIssues,
  crawlSummary: technicalData.crawlSummary
};

return [{ json: auditReport }];

