// Finalize audit with AI recommendations
const report = $('Compile Audit Report').first().json;
const aiRecommendations = $('AI Recommendations').first().json;

const finalReport = {
  ...report,
  recommendations: aiRecommendations,
  exportFormats: {
    pdf: `{{ $vars.REPORT_BASE_URL }}/audit/${report.auditId}/pdf`,
    csv: `{{ $vars.REPORT_BASE_URL }}/audit/${report.auditId}/csv`,
    json: `{{ $vars.REPORT_BASE_URL }}/audit/${report.auditId}/json`
  }
};

return [{ json: finalReport }];

