// Extract key website data
const html = $input.first().json.data || '';

// Basic meta extraction
const titleMatch = html.match(/<title>([^<]*)<\/title>/i);
const descMatch = html.match(/<meta name="description" content="([^"]*)"/i);
const keywordsMatch = html.match(/<meta name="keywords" content="([^"]*)"/i);

// Social links extraction
const socialPatterns = {
  instagram: /instagram\.com\/([\w.-]+)/i,
  linkedin: /linkedin\.com\/company\/([\w.-]+)/i,
  twitter: /twitter\.com\/([\w.-]+)/i,
  facebook: /facebook\.com\/([\w.-]+)/i
};

const socialLinks = {};
for (const [platform, pattern] of Object.entries(socialPatterns)) {
  const match = html.match(pattern);
  if (match) socialLinks[platform] = match[1];
}

// Tech stack hints
const techIndicators = {
  shopify: /cdn\.shopify\.com/i.test(html),
  wordpress: /wp-content/i.test(html),
  wix: /wix\.com/i.test(html),
  webflow: /webflow/i.test(html),
  nextjs: /_next/i.test(html),
  react: /react/i.test(html)
};

const detectedTech = Object.entries(techIndicators)
  .filter(([_, detected]) => detected)
  .map(([tech]) => tech);

return [{
  json: {
    title: titleMatch ? titleMatch[1] : 'Unknown',
    description: descMatch ? descMatch[1] : '',
    keywords: keywordsMatch ? keywordsMatch[1] : '',
    social_links: socialLinks,
    tech_stack: detectedTech,
    scraped_at: new Date().toISOString()
  }
}];

// Analyze Instagram data
const items = $input.all();
const posts = items.map(i => i.json);

if (posts.length === 0) {
  return [{ json: { error: 'No Instagram data available' } }];
}

// Calculate engagement metrics
const totalLikes = posts.reduce((sum, p) => sum + (p.likesCount || 0), 0);
const totalComments = posts.reduce((sum, p) => sum + (p.commentsCount || 0), 0);
const avgLikes = Math.round(totalLikes / posts.length);
const avgComments = Math.round(totalComments / posts.length);

// Content type analysis
const contentTypes = {
  image: posts.filter(p => p.type === 'Image').length,
  video: posts.filter(p => p.type === 'Video').length,
  carousel: posts.filter(p => p.type === 'Sidecar').length,
  reels: posts.filter(p => p.isSponsored === false && p.type === 'Video').length
};

// Posting frequency
const dates = posts.map(p => new Date(p.timestamp)).sort((a, b) => b - a);
const daysBetweenPosts = dates.length > 1 
  ? Math.round((dates[0] - dates[dates.length - 1]) / (1000 * 60 * 60 * 24) / posts.length)
  : 0;

// Hashtag analysis
const allHashtags = posts.flatMap(p => {
  const caption = p.caption || '';
  const matches = caption.match(/#[\w\u0080-\uFFFF]+/g) || [];
  return matches;
});

const hashtagFreq = {};
allHashtags.forEach(tag => {
  hashtagFreq[tag] = (hashtagFreq[tag] || 0) + 1;
});

const topHashtags = Object.entries(hashtagFreq)
  .sort((a, b) => b[1] - a[1])
  .slice(0, 15)
  .map(([tag, count]) => ({ tag, count }));

// Best performing posts
const topPosts = posts
  .sort((a, b) => (b.likesCount + b.commentsCount * 10) - (a.likesCount + a.commentsCount * 10))
  .slice(0, 5)
  .map(p => ({
    caption: (p.caption || '').substring(0, 100) + '...',
    likes: p.likesCount,
    comments: p.commentsCount,
    type: p.type
  }));

return [{
  json: {
    metrics: {
      total_posts_analyzed: posts.length,
      avg_likes: avgLikes,
      avg_comments: avgComments,
      engagement_rate: ((avgLikes + avgComments) / (posts[0]?.followersCount || 1) * 100).toFixed(2) + '%',
      posting_frequency: `Every ${daysBetweenPosts} days`
    },
    content_strategy: contentTypes,
    top_hashtags: topHashtags,
    top_performing_posts: topPosts,
    analyzed_at: new Date().toISOString()
  }
}];

